import React, { useEffect, useState } from 'react';
import { products } from './Local';

function FilterBox() {
  const [items, setItems] = useState([]);

  const sortAscending = () => {
    const sortedItems = [...products].sort((a, b) => a.id - b.id); // Creates a new sorted array
    setItems(sortedItems); // Updates state with the new sorted array
  };
const sortDescending = () => {
  const sortedItems = [...products].sort((a, b) => b.id - a.id);
  setItems(sortedItems);
};
useEffect(()=>{ setItems(products)},[])


return (
  <div style={styles.app}>
    <div style={styles.filter}>
      <button style={styles.button} onClick={sortAscending}>Acending</button>
      <button style={styles.button} onClick={sortDescending}>Decending</button>
    </div>
    <div style={styles.itemList}>
      <ul style={styles.ul}>
        {items.map((item, index) => (
          <li key={item.id} style={styles.li} >
            <h3>{item.id}</h3>
            <h3>{item.name}</h3>
            <p>{item.color}</p>
            <p>{item.brand}</p>
            <p><strong>{item.price}</strong></p>
          </li>
        ))}
      </ul>
    </div>
  </div>
);
}

const styles = {
  app: {
    display: 'flex',
    height: '100vh',
    margin: 0,
    fontFamily: 'Arial, sans-serif'
  },
  filter: {
    width: '20%',
    backgroundColor: '#f4f4f4',
    padding: '20px',
    boxShadow: '2px 0 5px rgba(0,0,0,0.1)'
  },
  itemList: {
    width: '80%',
    padding: '20px'
  },
  button: {
    padding: '10px 20px',
    backgroundColor: '#007BFF',
    color: '#fff',
    border: 'none',
    cursor: 'pointer',
    fontSize: '16px',
    borderRadius: '5px',
    margin: '5px'
  },
  ul: {
    listStyleType: 'none',
    padding: 0
  },
  li: {
    padding: '10px',
    borderBottom: '1px solid #ddd'
  }
};

export default FilterBox;
