import React, { useState } from 'react';

function FilterBox() {
  const [items, setItems] = useState(['Item 1', 'Item 2', 'Item 3', 'Item 4', 'Item 5']);
  const products = [
    {
      id: 1,
      name: "T-Shirt",
      size: "M",
      color: "Red",
      price: 19.99,
      brand: "BrandA"
    },
    {
      id: 2,
      name: "Jeans",
      size: "32",
      color: "Blue",
      price: 49.99,
      brand: "BrandB"
    },
    {
      id: 3,
      name: "Jacket",
      size: "L",
      color: "Black",
      price: 79.99,
      brand: "BrandC"
    },
    {
      id: 4,
      name: "Sneakers",
      size: "10",
      color: "White",
      price: 59.99,
      brand: "BrandD"
    },
    {
      id: 5,
      name: "Hat",
      size: "One Size",
      color: "Green",
      price: 14.99,
      brand: "BrandE"
    }
  ];

  console.log("products",products);

  const filterItems = () => {
    setItems(['Filtered Item 1', 'Filtered Item 2', 'Filtered Item 3']);
  };

  return (
    <div style={styles.app}>
      <div style={styles.filter}>
        <button style={styles.button} onClick={filterItems}>Filter Items</button>
      </div>
      <div style={styles.itemList}>
        <ul style={styles.ul}>
          {items.map((item, index) => (
            <li key={index} style={styles.li}>{item}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

const styles = {
  app: {
    display: 'flex',
    height: '100vh',
    margin: 0,
    fontFamily: 'Arial, sans-serif'
  },
  filter: {
    width: '20%',
    backgroundColor: '#f4f4f4',
    padding: '20px',
    boxShadow: '2px 0 5px rgba(0,0,0,0.1)'
  },
  itemList: {
    width: '80%',
    padding: '20px'
  },
  button: {
    padding: '10px 20px',
    backgroundColor: '#007BFF',
    color: '#fff',
    border: 'none',
    cursor: 'pointer',
    fontSize: '16px',
    borderRadius: '5px'
  },
  ul: {
    listStyleType: 'none',
    padding: 0
  },
  li: {
    padding: '10px',
    borderBottom: '1px solid #ddd'
  }
};

export default FilterBox;
